(1739, 1, 11, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1000000];\nlong long mod=1e9+7;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    priority_queue<pair<long long,long long>,vector<pair<long long,long long>>, greater<>> q;\n \n    long long n,a,b;\n    cin>>n>>a>>b;\n \n    long long arr[n+3];\n    for (int i=1; i<=n; i++) {\n        cin>>arr[i];\n        f[i]+=f[i-1]+arr[i];\n    }\n    long long maxn=-1e18;\n    long long l=0;\n    for (int i=a; i<=n; i++){\n        q.push({f[l],l});\n        l++;\n        while (!q.empty() && q.top().second + b < i) q.pop();\n        maxn=max(maxn,f[i]-q.top().first);\n    }\n    cout<<maxn;\n}', 'AC', 0.015625, 4476, 25, 25, '2025-06-25 19:06:27'),
