(1743, 1, 14, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1001][1001];\nlong long mod=1e9+7;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    long long n;\n    cin>>n;\n    for (int i=1; i<=n; i++) \n    for (int j=1; j<=n; j++){\n        char x;\n        cin>>x;\n        if (i==1 && j==1) {\n            if (x==\'.\') f[1][1]=1;\n        }\n        if (x==\'.\') f[i][j]=(f[i][j]+f[i-1][j]+f[i][j-1])%mod;\n    }\n    cout<<f[n][n];\n}', 'AC', 0.015625, 4476, 15, 15, '2025-06-25 19:15:39'),
(1744, 1, 15, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[100001];\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    long long n,x;\n    cin>>n>>x;\n    long long a[n+3],b[n+3];\n    \n    for (int i=1; i<=n; i++) cin>>a[i];\n    for (int i=1; i<=n; i++) cin>>b[i];\n    for (int i=1; i<=x; i++) f[i]=-1;\n    for (int i=1; i<=n; i++)\n    for (int j=x-a[i]; j>=0; j--)\n        if (f[j]!=-1)\n            f[j+a[i]] = max(f[j+a[i]],f[j]+b[i]);\n \n    long long maxn=0;\n    for (int i=1; i<=x; i++) maxn=max(maxn,f[i]);\n    cout<<maxn;\n    \n}', 'AC', 0.015625, 4476, 15, 15, '2025-06-25 19:18:02'),
(1745, 1, 16, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1000000];\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    long long n;\n    cin>>n;\n    long long a[n+3];\n    for (int i=1; i<=n; i++) cin>>a[i];\n    sort(a+1,a+n+1);\n    f[0]=1;\n    vector<int> v;\n    \n    for (int i=1; i<=1e5; i++) f[i]=-1;\n \n    for (int i=1; i<=n; i++)\n    for (int j=1e5-a[i]; j>=0; j--)\n    if (f[j]!=-1) \n        f[j+a[i]]=max(f[j+a[i]], f[j]+1);\n \n    for (int i=1; i<=1e5; i++)\n    if (f[i]!=-1) v.push_back(i);\n    cout<<v.size()<<\"\\n\";\n    for (auto x:v) cout<<x<<\" \";\n    \n}', 'AC', 0.03125, 4476, 13, 13, '2025-06-25 19:20:52'),
(1746, 1, 17, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nbool vis[5001][5001];\nlong long f[5001],a[5001],dp[5001][5001];\nlong long get(int l,int r){\n    if (vis[l][r]) return dp[l][r];\n    if (l==r) return a[l];\n    vis[l][r]=true;\n    return dp[l][r] = max(a[l] + (f[r]-f[l]) - get(l+1,r) , a[r] + (f[r-1]-f[l-1]) - get(l,r-1));\n}\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    long long n;\n    cin>>n;\n    for (int i=1; i<=n; i++) {\n        cin>>a[i];\n        f[i]=f[i-1]+a[i];\n    }\n    \n    cout<<get(1,n);\n    \n}', 'AC', 0.015625, 4476, 15, 15, '2025-06-25 19:22:55'),
(1747, 1, 18, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint a[1001][1001];\nint movex[]={0,0,1,-1};\nint movey[]={1,-1,0,0};\nint res=0;\nint n,m;\nvoid bfs(int x,int y){\n    queue <int> q_x;\n    queue <int> q_y;\n    int d1=0,d2=0;\n    q_x.push(x);\n    q_y.push(y);\n    res++;\n    while (!q_x.empty()){\n        int u=q_x.front();\n        int v=q_y.front();\n        q_x.pop();\n        q_y.pop();\n        for (int i=0; i<=3; i++){\n            int t1=u+movex[i];\n            int t2=v+movey[i];\n            if (0<t1 && t1<=n && 0<t2 && t2<=m && !a[t1][t2]){\n                q_x.push(t1);\n                q_y.push(t2);\n                a[t1][t2]=1;\n            }\n        }\n    }\n}\nint main()\n{\n    cin>>n>>m;\n    for (int i=1; i<=n; i++){\n        string s;\n        cin>>s;\n        for (int j=0; j<m; j++)\n        if (s[j]==\'.\') a[i][j+1]=0; else\n        if (s[j]==\'#\') a[i][j+1]=1;\n        \n    }\n    for (int i=1; i<=n; i++)\n    for (int j=1; j<=m; j++) \n    if (!a[i][j]) bfs(i,j);\n    cout<<res;\n    return 0;\n}', 'AC', 0.015625, 4472, 19, 19, '2025-06-25 19:25:14'),
