(1749, 1, 19, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long tree[10000000],n;\nvoid build(int id,int l,int r){\n    if (l==r){\n        tree[id]=1;\n        return;\n    }\n    int mid=(l+r)/2;\n    build(id*2,l,mid);\n    build(id*2+1,mid+1,r);\n    tree[id] = tree[id*2] + tree[id*2+1];\n}\nint get(int id,int l,int r,int u,int v){\n    if (r<u || v<l) return 0;\n    if (u<=l && r<=v) return tree[id];\n    int mid=(l+r)/2;\n    return get(id*2,l,mid,u,v) + get(id*2+1,mid+1,r,u,v);\n}\nvoid update(int id,int l,int r,int pos){\n    if (l==r){\n        tree[id]=0;\n        return;\n    }\n    int mid=(l+r)/2;\n    if (pos<=mid)\n        update(id*2,l,mid,pos);\n    else\n        update(id*2+1,mid+1,r,pos);\n    tree[id] = tree[id*2] + tree[id*2+1];\n}\nint query(int l,int r,int x){\n    if (l==r)\n        return l;\n    int mid=(l+r)/2;\n    int temp=get(1,1,n,1,mid);\n    if (temp<x)\n        return query(mid+1,r,x);\n    return query(l,mid,x);\n}\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    cin>>n;\n    long long a[n+3],b[n+3];\n    for (int i=1 ;i<=n; i++) cin>>a[i];\n    build(1,1,n);\n \n    for (int i=1 ;i<=n; i++){\n        long long x;\n        cin>>x;\n        // int l=1,r=n;\n        // while (l<r){\n        //     int mid=(l+r)/2;\n        //     int temp = get(1,1,n,1,mid);\n        //     if (temp>x) r=mid-1; else  l=mid+1; \n        // }\n        // int mid=(l+r)/2;\n        // if (mid==0) mid=1;\n        // if (mid>n) mid=n;\n        // while (get(1,1,n,1,mid)<x) mid++;\n        // while (mid!=1 && get(1,1,n,1,mid-1)>=x) mid--;\n        int mid=query(1,n,x);\n        cout<<a[mid]<<\" \";\n        update(1,1,n,mid); \n    }\n    \n    \n}', '', 0.015625, 1784, 10, 6, '2025-06-26 01:04:34'),
