(1763, 1, 8, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long a[1000000],n,k;\nbool  get(long long mid){\n    long long res=0;\n    for (int i=1; i<=n; i++) {\n        res+=mid/a[i];\n        if (res>=k) return true;\n    }\n    return false;\n}\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    cin>>n>>k;\n    for (int i=1; i<=n; i++) cin>>a[i];\n    long long l=1,r=1e18;\n    while (l<r) {\n        long long mid=(l+r)/2;\n        bool kt = get(mid);\n        if (!kt) l=mid+1; else r=mid-1;\n    }\n    long long mid=(l+r)/2;\n    if (mid<=0) mid=1;\n    while (!get(mid)) mid++;\n    if (mid!=1)\n        while (true){\n            if (get(mid-1)) mid--; else break;\n        }\n    cout<<mid;\n}', 'AC', 0.015625, 4516, 20, 20, '2025-06-26 01:32:40'),
(1764, 1, 9, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    long long n;\n    cin>>n;\n    map<long long,long long> m;\n    m[0]++;\n    long long res=0;\n    long long s=0;\n    for (int i=1; i<=n; i++){\n        long long x;\n        cin>>x;\n        s+=x;\n        s%=n;\n        \n        if (s==0) res+=m[0]; else {\n            res+=m[s];\n            if (s<0) res+=m[n+s]; else res+=m[-(n-s)];\n        }\n        m[s]++;\n        // cout<<s<<\" \";\n \n    }\n    cout<<res;\n}', 'AC', 0.046875, 4512, 11, 11, '2025-06-26 01:33:14'),
(1765, 1, 10, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    long long n,k;\n    cin>>n>>k;\n    long long a[n+3];\n    for (int i=1; i<=n; i++) cin>>a[i];\n    \n    long long res=0;\n    map<long long,long long> m;\n    long long l=1;\n    m[a[1]]++;\n    res=1;\n    long long d=1;\n    for (int i=2; i<=n; i++) {\n        m[a[i]]++;\n        if (m[a[i]]==1) d++;\n        while (d>k) {\n            m[a[l]]--;\n            if (m[a[l]]==0) d--;\n            l++;\n        }\n        res+=i-l+1;\n    }\n    cout<<res;\n}', 'AC', 0.03125, 4404, 11, 11, '2025-06-26 01:34:02'),
(1766, 1, 11, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1000000];\nlong long mod=1e9+7;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    priority_queue<pair<long long,long long>,vector<pair<long long,long long>>, greater<>> q;\n \n    long long n,a,b;\n    cin>>n>>a>>b;\n \n    long long arr[n+3];\n    for (int i=1; i<=n; i++) {\n        cin>>arr[i];\n        f[i]+=f[i-1]+arr[i];\n    }\n    long long maxn=-1e18;\n    long long l=0;\n    for (int i=a; i<=n; i++){\n        q.push({f[l],l});\n        l++;\n        while (!q.empty() && q.top().second + b < i) q.pop();\n        maxn=max(maxn,f[i]-q.top().first);\n    }\n    cout<<maxn;\n}', 'AC', 0.015625, 4516, 25, 25, '2025-06-26 01:34:42'),
(1767, 1, 12, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1000001];\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    long long n,x;\n    cin>>n>>x;\n    long long a[n+3];\n    for (int i=1; i<=n; i++) cin>>a[i];\n    sort(a+1,a+n+1);\n    for (int i=1; i<=x; i++) f[i]=1e18;\n    for (int i=1; i<=x; i++)\n    for (int j=1; j<=n; j++)\n    if (a[j]<=i) f[i]=min(f[i],f[i-a[j]]+1);\n \n    if (f[x]==1e18) cout<<-1; else\n    cout<<f[x];\n \n}', 'AC', 0.015625, 1792, 18, 18, '2025-06-26 01:35:07'),
(1768, 1, 13, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint  get(int n){\n    int maxn=0;\n    do {\n        maxn=max(maxn,n%10);\n        n/=10; \n    } while (n!=0);\n    return maxn;\n}\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    int n;\n    cin>>n;\n    long long res=0;\n    do {\n        n-=get(n);\n        res++;\n    } while (n);\n    cout<<res;\n}', 'AC', 0, 1788, 14, 14, '2025-06-26 01:35:42'),
(1769, 1, 14, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1001][1001];\nlong long mod=1e9+7;\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n \n    long long n;\n    cin>>n;\n    for (int i=1; i<=n; i++) \n    for (int j=1; j<=n; j++){\n        char x;\n        cin>>x;\n        if (i==1 && j==1) {\n            if (x==\'.\') f[1][1]=1;\n        }\n        if (x==\'.\') f[i][j]=(f[i][j]+f[i-1][j]+f[i][j-1])%mod;\n    }\n    cout<<f[n][n];\n}', 'AC', 0.015625, 4520, 15, 15, '2025-06-26 01:36:18'),
(1770, 1, 15, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[100001];\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    long long n,x;\n    cin>>n>>x;\n    long long a[n+3],b[n+3];\n    \n    for (int i=1; i<=n; i++) cin>>a[i];\n    for (int i=1; i<=n; i++) cin>>b[i];\n    for (int i=1; i<=x; i++) f[i]=-1;\n    for (int i=1; i<=n; i++)\n    for (int j=x-a[i]; j>=0; j--)\n        if (f[j]!=-1)\n            f[j+a[i]] = max(f[j+a[i]],f[j]+b[i]);\n \n    long long maxn=0;\n    for (int i=1; i<=x; i++) maxn=max(maxn,f[i]);\n    cout<<maxn;\n    \n}', 'AC', 0.03125, 4520, 15, 15, '2025-06-26 01:36:41'),
(1771, 1, 16, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nlong long f[1000000];\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    long long n;\n    cin>>n;\n    long long a[n+3];\n    for (int i=1; i<=n; i++) cin>>a[i];\n    sort(a+1,a+n+1);\n    f[0]=1;\n    vector<int> v;\n    \n    for (int i=1; i<=1e5; i++) f[i]=-1;\n \n    for (int i=1; i<=n; i++)\n    for (int j=1e5-a[i]; j>=0; j--)\n    if (f[j]!=-1) \n        f[j+a[i]]=max(f[j+a[i]], f[j]+1);\n \n    for (int i=1; i<=1e5; i++)\n    if (f[i]!=-1) v.push_back(i);\n    cout<<v.size()<<\"\\n\";\n    for (auto x:v) cout<<x<<\" \";\n    \n}', 'AC', 0.015625, 4636, 13, 13, '2025-06-26 01:37:30'),
(1772, 1, 17, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nbool vis[5001][5001];\nlong long f[5001],a[5001],dp[5001][5001];\nlong long get(int l,int r){\n    if (vis[l][r]) return dp[l][r];\n    if (l==r) return a[l];\n    vis[l][r]=true;\n    return dp[l][r] = max(a[l] + (f[r]-f[l]) - get(l+1,r) , a[r] + (f[r-1]-f[l-1]) - get(l,r-1));\n}\nint main()\n{\n    ios_base::sync_with_stdio(false);\n    cin.tie(NULL);\n    cout.tie(NULL);\n    \n    long long n;\n    cin>>n;\n    for (int i=1; i<=n; i++) {\n        cin>>a[i];\n        f[i]=f[i-1]+a[i];\n    }\n    \n    cout<<get(1,n);\n    \n}', 'AC', 0.015625, 4520, 15, 15, '2025-06-26 01:38:07'),
(1773, 1, 18, NULL, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint a[1001][1001];\nint movex[]={0,0,1,-1};\nint movey[]={1,-1,0,0};\nint res=0;\nint n,m;\nvoid bfs(int x,int y){\n    queue <int> q_x;\n    queue <int> q_y;\n    int d1=0,d2=0;\n    q_x.push(x);\n    q_y.push(y);\n    res++;\n    while (!q_x.empty()){\n        int u=q_x.front();\n        int v=q_y.front();\n        q_x.pop();\n        q_y.pop();\n        for (int i=0; i<=3; i++){\n            int t1=u+movex[i];\n            int t2=v+movey[i];\n            if (0<t1 && t1<=n && 0<t2 && t2<=m && !a[t1][t2]){\n                q_x.push(t1);\n                q_y.push(t2);\n                a[t1][t2]=1;\n            }\n        }\n    }\n}\nint main()\n{\n    cin>>n>>m;\n    for (int i=1; i<=n; i++){\n        string s;\n        cin>>s;\n        for (int j=0; j<m; j++)\n        if (s[j]==\'.\') a[i][j+1]=0; else\n        if (s[j]==\'#\') a[i][j+1]=1;\n        \n    }\n    for (int i=1; i<=n; i++)\n    for (int j=1; j<=m; j++) \n    if (!a[i][j]) bfs(i,j);\n    cout<<res;\n    return 0;\n}', 'AC', 0.046875, 4520, 19, 19, '2025-06-26 01:38:49'),
(1774, 1, 1, 13, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint main()\n{\n   ios_base::sync_with_stdio(false);\n   cin.tie(NULL);\n   cout.tie(NULL);\n \n   long long n;\n   cin>>n;\n   while (n!=1) {\n      cout<<n<<\" \";\n      n=(n%2==0) ? n/2 : n*3+1;\n   }\n   cout<<n;\n}', 'AC', 0.015625, 1828, 14, 14, '2025-06-26 01:39:51'),
(1775, 1, 2, 13, 'cpp', '#include <bits/stdc++.h>\nusing namespace std;\nint main()\n{\n   ios_base::sync_with_stdio(false);\n   cin.tie(NULL);\n   cout.tie(NULL);\n \n   long long n;\n   cin>>n;\n   long long s=n*(n+1)/2;\n   n--;\n   while (n--){\n      long long x;\n      cin>>x;\n      s-=x;\n   }\n   cout<<s;\n}', 'AC', 0.015625, 4512, 14, 14, '2025-06-26 01:40:37');
